//map layout
import React, { Component } from 'react';
import { Map, GoogleApiWrapper } from 'google-maps-react';
import './index.css';
// my key 'AIzaSyC-FNgVzK38CFoYC6E4-olIUu418f90H2I'

const mapStyles = {
  width: '67.2%',
  height: '97.8%'
};

const Marker = ({ text }: any) => <div>{text}</div>;

class MapContainer extends Component {
  constructor(props) {
    super(props);
    this.state = {
      center: {lat: 43.077783, lng: -89.411236},
      places: [{lat: 47.9, lng: -122.14}]
    }
  }

  // displayMarkers = () => {
  //   return this.state.places.map((store, index) => {
  //     return <Marker key={index} id={index} position={{
  //      lat: store.latitude,
  //      lng: store.longitude
  //    }}
  //    onClick={() => console.log("You clicked me!")} />
  //   })
  // }

  componentDidMount() {
    navigator.geolocation.getCurrentPosition(
      position => {
        this.setState({center: { lat: position.coords.latitude, lng: position.coords.longitude }});
      },
      error => console.log(error)
    );
  }

  render() {
    return (
      // you must set the container height explicitly
      <div style={{ height: '100%', width: '100%' }}>
        <Map
          //google={this.props.google}
          center={this.state.center}
          style={mapStyles}
          zoom={this.props.zoom}
        >
        <Marker
          // lat={43.077}
          // lng={-89.411}
          lat={this.state.center.lat}
          lng={this.state.center.lng}
          text="My Marker"
        />
        //{this.displayMarkers()}
        </Map>
      </div>
    );
  }
}

export default GoogleApiWrapper({
  apiKey: 'AIzaSyC-FNgVzK38CFoYC6E4-olIUu418f90H2I'
})(MapContainer);
