//map layout
import React, { Component } from 'react';
import { Map, GoogleApiWrapper, Marker, InfoWindow } from 'google-maps-react';
import axios from 'axios';
import './index.css';
// my maps api key 'AIzaSyC-FNgVzK38CFoYC6E4-olIUu418f90H2I'

const mapStyles = {
  width: '68.7%',
  height: '97.2%'
};

class MapContainer extends Component {
  state = {
    showingInfoWindow: false,  //Hides or the shows the infoWindow
    activeMarker: {},          //Shows the active marker upon click
    selectedPlace: {},          //Shows the infoWindow to the selected place upon a marker
    places: null,
    center: {lat: null, lng: null}
  };

  // constructor(props) {
  //   super(props);
  //     this.state = {
  //       center: [{latitude: 44.0377, longitude: -90.5477}],
  //       places: [{latitude: 47.9, longitude: -122.14}]
  //     };
  // }

  getPlaces(searchterm) {
    let config = {
      headers: {
        "Authorization":'TQl-ldodqDuIn-Wi3xJ5xkB-nsuPgeUvlti5NCOhIJPKTssK0BA5EjYWnwasmMSca20hq1UxyNSYPLL1c1URFMrztx4w9VfRXcOiP8f0XlWgSxZMRvIKQYlO6htPXnYx',
        "Access-Control-Allow-Origin": "*"},
      params: {
        term: searchterm,
        latitude: this.state.center.lat,
        longitude: this.state.center.lng,
        radius: 50
      },
      responseType: 'json',
    };

    axios.get('https://api.yelp.com/v3/businesses/search', config)
      .then(response => {
        this.setState({ places: response.data });
        console.log(response)
      });
  }

  onMarkerClick = (props, marker, e) =>
  this.setState({
    selectedPlace: props,
    activeMarker: marker,
    showingInfoWindow: true
  });

  onClose = props => {
    if (this.state.showingInfoWindow) {
      this.setState({
        showingInfoWindow: false,
        activeMarker: null
      });
    }
  };

  displayMarkers = () => {
    return this.state.places.map((store, index) => {
      return <Marker key={index} id={index} position={{
       lat: store.latitude,
       lng: store.longitude
     }}
     onClick={() => console.log("You clicked me!")} />
    })
  }

  componentDidMount() {
    navigator.geolocation.getCurrentPosition(
      pos => {
        const coords = pos.coords;
        this.setState({
          center: {
            lat: coords.latitude,
            lng: coords.longitude
          }
        });
      },
      error => console.log(error)
    );
  }

  render() {
    this.getPlaces(this.props.craving)
    return (
      // you must set the container height explicitly
      <div>
        <Map
          google={this.props.google}
          center={this.state.center}
          style={mapStyles}
          zoom={13}
        >
          <Marker
            id={'user-location'}
            onClick={this.onMarkerClick}
            name={'You are here'}
            position={this.state.center}
            color="blue"
          />

          <InfoWindow
            marker={this.state.activeMarker}
            visible={this.state.showingInfoWindow}
            onClose={this.onClose}
          >
          <div>
            <h4>{this.state.selectedPlace.name}</h4>
          </div>
          </InfoWindow>
        </Map>
      </div>
    );
  }
}

export default GoogleApiWrapper({
  apiKey: 'AIzaSyC-FNgVzK38CFoYC6E4-olIUu418f90H2I'
})(MapContainer);
