//map layout
import React, { Component } from 'react';
import { Map, GoogleApiWrapper, Marker } from 'google-maps-react';
//import { geolocated } from "react-geolocated";
import './index.css';
// my key 'AIzaSyC-FNgVzK38CFoYC6E4-olIUu418f90H2I'

const mapStyles = {
  width: '67.2%',
  height: '97.6%'
};

const currentLocation = ({ text }) => <div>{text}</div>;

class MapContainer extends Component {

  constructor(props) {
    super(props);
    this.state = {
      center: {lat: 43.077783, lng: -89.411236},
      stores: [{lat: 43.077783, lng: -89.411236}]
    }
  }

  displayMarkers = () => {
    return this.state.stores.map((store, index) => {
      return <Marker key={index} id={index} position={{
       lat: store.latitude,
       lng: store.longitude
     }}
     onClick={() => console.log("You clicked me!")} />
    })
  }

  componentDidUpdate() {
    navigator.geolocation.getCurrentPosition(
      position => {
        this.setState({center: { lat: position.coords.latitude, lng: position.coords.longitude }});

      },
      error => console.log(error)
    );
  }

  render() {
    return (
      // you must set the container height explicitly
      <div>
        <Map
          className={'map'}
          google={this.props.google}
          center={this.state.center}
          style={mapStyles}
          zoom={this.props.zoom}
        >
          <AnyReactComponent
            lat={43.077783}
            lng={-89.411236}
            text="My Marker"
          />
          {this.displayMarkers()}
        </Map>
      </div>
    );
  }
}

export default GoogleApiWrapper({
  apiKey: 'AIzaSyC-FNgVzK38CFoYC6E4-olIUu418f90H2I'
})(MapContainer);
