//options class
import React, { Component } from 'react';
// import Button from 'react-bootstrap/Button';
import AwesomeButtonStyles from "react-awesome-button/src/styles/styles.scss";
import { AwesomeButton } from "react-awesome-button";
//import ReactDOM from 'react-dom';
import './index.css';

class Options extends Component {
  renderButton(props) {
    return (
      <AwesomeButton
        cssModule={AwesomeButtonStyles}
        type="primary"
        ripple
        onPress={() => {
          // do something
        }}
      >
      {this.props}
      </AwesomeButton>
    );
  }

  render() {
    let prompt = 'What are you craving?'
    return (
      <div id="options">
        <div className="prompt">{prompt}</div>
        {this.renderButton()}
        <AwesomeButton type="primary">Coffee</AwesomeButton>
        <AwesomeButton type="primary">Tea</AwesomeButton>
        <AwesomeButton type="primary">Bubble Tea</AwesomeButton>
        <AwesomeButton type="primary">Cheese Curds</AwesomeButton>
        <AwesomeButton type="primary">Ice Cream</AwesomeButton>
        <AwesomeButton type="primary">Sweets</AwesomeButton>
      </div>

    );
  }
}

export default Options;
